content.text:
{
  "核心的メッセージ": "伝わるかは内容より言い方で決まる。相手の頭の中にある“得”に寄せ、強い言葉の型で言い換えれば、NoはYesに変わる。",
  "エグゼクティブ・サマリー": {
    "問い": "なぜ同じ内容でも、ある言い方は伝わり、別の言い方は伝わらないのか？",
    "答え": "人は自分の利益に反応し、記憶に残る“強い型”で語られたメッセージに動くから。",
    "Why": "脳は損得と新奇性に強く反応し、注意・理解・記憶・行動の各段階で選別が起きる。",
    "How": "1) 自分の言いたいことを一度捨てる 2) 相手の頭の中（利得・不安）を想像する 3) 相手の得に言い換える＋強い言葉の型（ギャップ・サプライズ・リピート・ナンバー・比喩・体言止め等）で表現する。"
  },
  "主要概念の説明": [
    {
      "概念": "Yesに変える3ステップ",
      "定義": "自分の主張をそのまま言わず、相手の頭の中を想像し、相手の利益へ言い換える手順。",
      "一言要約": "主語を“自分”から“相手”へ"
    },
    {
      "概念": "相手の利益（ベネフィット）",
      "定義": "相手が得られる具体的なリターンや負担減。機能ではなく効果に焦点。",
      "一言要約": "相手にとっての“得”"
    },
    {
      "概念": "ギャップ法",
      "定義": "現状と理想、常識と逆説の差を示し注意を惹く技法。",
      "一言要約": "差で引きつける"
    },
    {
      "概念": "サプライズ法",
      "定義": "予想外の切り口や数字で認知を揺らし、耳目を集める。",
      "一言要約": "意外性で刺す"
    },
    {
      "概念": "リピート法",
      "定義": "重要語を繰り返し、リズムと記憶定着を生む。",
      "一言要約": "反復で残す"
    },
    {
      "概念": "ナンバー法",
      "定義": "数値・順位・分量で具体性と信頼を担保する。",
      "一言要約": "数字で確かに"
    },
    {
      "概念": "比喩法",
      "定義": "未知を既知に橋渡しし、理解を一瞬で進める表現。",
      "一言要約": "わかるに変換"
    },
    {
      "概念": "体言止め",
      "定義": "文末を名詞で止め余韻と強度を作る。",
      "一言要約": "余韻で強く"
    },
    {
      "概念": "選択肢提示（二択化）",
      "定義": "Yes/NoではなくA/Bの選択に変え、合意可能域を広げる。",
      "一言要約": "Noを回避"
    },
    {
      "概念": "共感・認可",
      "定義": "相手の立場や感情を言語化し、心理的抵抗を下げる。",
      "一言要約": "まず認める"
    }
  ],
  "概念間の関係性や因果関係": [
    "相手理解 → ベネフィット抽出 → 言い換え → 承諾率↑",
    "ギャップ/サプライズ → 注意喚起 → メッセージ受容↑",
    "リピート/ナンバー/比喩 → 理解・記憶↑ → 再現性↑",
    "共感・認可 → 心理的抵抗↓ → 選択肢提示の効果↑",
    "体言止め → 余韻・情動喚起 → 行動意図↑"
  ],
  "構造と論理展開": [
    {
      "区分": "序章: なぜ伝え方が9割か",
      "目的": "言い方が成果を左右する実例で問題意識を喚起。",
      "内容要点": "同じ内容でも成果差が生まれるメカニズムを提示。"
    },
    {
      "区分": "第1部: Yesに変える技術",
      "目的": "拒否が前提の状況で合意を得る基本手順を示す。",
      "内容要点": "自分基点→相手基点への転換／ベネフィット化／共感・認可／二択化・限定。"
    },
    {
      "区分": "第2部: 強いコトバの作り方",
      "目的": "記憶に残し行動を促す表現レシピを提供。",
      "内容要点": "ギャップ・サプライズ・リピート・ナンバー・比喩・体言止めの型と例。"
    },
    {
      "区分": "第3部: 応用と実践",
      "目的": "仕事・恋愛・交渉・プレゼンへの落とし込み。",
      "内容要点": "場面別テンプレ／添削例で型の当てはめ方を訓練。"
    },
    {
      "区分": "終章: 習慣化",
      "目的": "日常での反復と改善の仕組み化。",
      "内容要点": "メモ化→言い換え→検証→改善のループを推奨。"
    }
  ],
  "重要な引用": [
    {
      "原文/要旨": "NoをYesに変えるのは、才能ではなく技術。",
      "なぜ重要か": "学習可能性を示し、実践意欲を高めるから。"
    },
    {
      "原文/要旨": "人は自分の得になる言葉に動く。",
      "なぜ重要か": "主語を相手に切り替える指針になる。"
    },
    {
      "原文/要旨": "強い言葉は短い。",
      "なぜ重要か": "冗長さより簡潔さが記憶と行動を促すため。"
    },
    {
      "原文/要旨": "正解は相手の中にある。",
      "なぜ重要か": "相手理解の徹底が出発点だと示す。"
    },
    {
      "原文/要旨": "常識をズラすと、人は振り向く。",
      "なぜ重要か": "ギャップとサプライズの核心を端的に表す。"
    }
  ],
  "今日できるアクション": [
    "お願いを相手利益に言い換える",
    "ギャップ法で件名を3案作る",
    "二択の提案文を3通作成"
  ],
  "関連書籍": [
    {
      "書名": "伝え方が9割 2",
      "著者": "佐々木圭一",
      "関連性": "同シリーズの拡張版で実践例と型が増える。"
    },
    {
      "書名": "影響力の武器",
      "著者": "ロバート・B・チャルディーニ",
      "関連性": "承諾の心理トリガーが伝え方の土台になる。"
    },
    {
      "書名": "人を動かす",
      "著者": "デール・カーネギー",
      "関連性": "共感・認可など対人原則が一致。"
    },
    {
      "書名": "ザ・コピーライティング",
      "著者": "ジョン・ケープルズ",
      "関連性": "ギャップ・数字・ベネフィットの具体技法が学べる。"
    },
    {
      "書名": "影響力の武器 実践編",
      "著者": "ロバート・B・チャルディーニ",
      "関連性": "状況別の適用が伝え方の精度を上げる。"
    }
  ]
}
